package baisc_Interger_Program;

public class SwapTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 		int a=100,b=200;
 		System.out.println("a=" +a+ "  " +"b=" +b);
         a=a+b;
         b=a-b;
         a=a-b;
 		System.out.println(" After Swap a=" +a+ "  " +"b=" +b);
	}

}
